import requests
import json

# URL do seu endpoint
url = "http://localhost:8000/CmykPredictor"

# Dados de imagem em base64 (substitua por sua imagem em base64)
dados_imagem_base64 = ""

# Definindo o payload com o campo 'image_base64'
payload = {"image_base64": dados_imagem_base64}

# Envia a requisição POST com o body JSON
response = requests.post(url, json=payload)

# Verifica se a requisição foi bem-sucedida
if response.status_code == 200:
    print("Resposta:", response.json())  # Exibe a resposta do servidor
else:
    print("Erro na requisição:", response.status_code)
